    private void showTabel(){
        int row = jTableKeranjang.getRowCount();
        for(int i=0; i<row; i++){
            tabelmodel.removeRow(0);
        }
        try{
            con = Koneksi.getConnection();
            stm = con.createStatement();
            String SQL = ("select * from keranjang order by id_keranjang desc");
            rs = stm.executeQuery(SQL);
            while(rs.next()){
                String data[] = {rs.getString(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getString(5),rs.getString(6),rs.getString(7)};
                tabelmodel.addRow(data);
            }
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
    }
    
    private void setTotal(){
        setDiskon();
        try{
            con = Koneksi.getConnection();
            stm = con.createStatement();
            String SQL = "select sum(total) from keranjang";
            rs = stm.executeQuery(SQL);
            int total = 0;
            int diskon = Integer.parseInt(jLabelDiskon.getText());
            while(rs.next()){
                total = rs.getInt("sum(total)");
            }
            int total_diskon = total-diskon;
            jLabelTotal.setText(Integer.toString(total_diskon));
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
    }
    
    private void setDiskon(){
        try{
            con = Koneksi.getConnection();
            stm = con.createStatement();
            String SQL = "select sum(jumlah) from keranjang where kode_barang like 'A%'";
            rs = stm.executeQuery(SQL);
            while(rs.next()){
                if(rs.getInt("sum(jumlah)") > 10){
                    String diskon = ""+rs.getInt("sum(jumlah)")*20000;
                    jLabelDiskon.setText(diskon);
                }
                else{
                    jLabelDiskon.setText("0");
                }
            }
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
    }

