    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        // TODO add your handling code here:
        setDiskon();
        setTotal();
        if(jNama.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "Form Nama Tidak Boleh Kosong!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jNama.requestFocus();
        }
        else if(jAlamat.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "Form Alamat Tidak Boleh Kosong!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jAlamat.requestFocus();
        }
        else if(jTanggalPengembalian.getDate() == null){
            JOptionPane.showMessageDialog(null, "Form Tanggal Pengembalian Tidak Boleh Kosong!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jTanggalPengembalian.requestFocus();
        }
        else if(jBayar.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "Form Bayar Tidak Boleh Kosong!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jBayar.requestFocus();
        }
        else if(! jNama.getText().matches("[ a-zA-Z]*")){
            JOptionPane.showMessageDialog(null, "Form Nama Tidak Boleh Angka!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jNama.requestFocus();
        }
        else if(!jBayar.getText().matches("[0-9]*")){
            JOptionPane.showMessageDialog(null, "Form Bayar Tidak Boleh Karakter!!!", "Failure", JOptionPane.ERROR_MESSAGE);
            jBayar.requestFocus();
        }
        else if(jBayar.getText().matches("[0-9]*")){
            int total1 = Integer.parseInt(jLabelTotal.getText());
            int bayar1 = Integer.parseInt(jBayar.getText());
            if(bayar1 < total1){
                JOptionPane.showMessageDialog(null, "Uang Pembayaran Kurang!!!", "Failure", JOptionPane.ERROR_MESSAGE);
                jBayar.requestFocus();
            }
            else{
                try{
                    con = Koneksi.getConnection();
                    pst = con.prepareStatement("insert into penyewa values(null,?,?)");
                    pst.setString(1, jNama.getText());
                    pst.setString(2, jAlamat.getText());
                    pst.executeUpdate();

                    pst2 = con.prepareStatement("insert into transaksi values(null,?,?,?,?,?,?,?,?)");

                    java.util.Date tanggal_kembali = (java.util.Date) this.jTanggalPengembalian.getDate();

                    Date tgl = new Date();
                    SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");
                    String tanggal = dateFormat.format(tgl);

                    pst2.setString(1, tanggal);
                    pst2.setString(2, tanggal);
                    pst2.setDate(3, new java.sql.Date(tanggal_kembali.getTime()));
                    pst2.setInt(4, 0);
                    pst2.setString(5, jLabelTotal.getText());
                    pst2.setString(6, jLabelDiskon.getText());
                    pst2.setString(7, jBayar.getText());
                    int bayar = Integer.parseInt(jBayar.getText());
                    int total = Integer.parseInt(jLabelTotal.getText());
                    int kembalian = bayar-total;
                    pst2.setInt(8, kembalian);
                    pst2.executeUpdate();

                    stm = con.createStatement();
                    String SQL = "select id_transaksi from transaksi order by id_transaksi desc limit 1";
                    rs = stm.executeQuery(SQL);
                    String id_transaksi = null;
                    while(rs.next()){
                        id_transaksi = rs.getString("id_transaksi");
                    }

                    String SQL2 = "select id_penyewa from penyewa order by id_penyewa desc limit 1";
                    rs2 = stm.executeQuery(SQL2);
                    String id_penyewa = null;
                    while(rs2.next()){
                        id_penyewa = rs2.getString("id_penyewa");
                    }



                    String SQL3 = "select * from keranjang";
                    rs3 = stm.executeQuery(SQL3);
                    pst3 = con.prepareStatement("insert into sewa values(null,?,?,?,?,?,?)");
                    while(rs3.next()){
                        pst3.setString(1, rs3.getString("jumlah"));
                        pst3.setString(2, rs3.getString("total"));
                        pst3.setString(3, rs3.getString("kode_barang"));
                        pst3.setString(4, id_transaksi);
                        pst3.setString(5, id_penyewa);
                        pst3.setString(6, "Disewa");
                        pst3.executeUpdate();
                    }

                    con.createStatement().executeUpdate("delete from keranjang");
                    String []tabelHeader = {"ID","Kode Barang","Nama Barang","Jenis Barang","Harga Sewa","Jumlah","Total"};
                    tabelmodel = new DefaultTableModel(tabelHeader, 0);
                    jTableKeranjang.setModel(tabelmodel);
                    showTabel();
                    setTotal();
                    setDiskon();
                    jNama.setText(null);
                    jAlamat.setText(null);
                    jTanggalPengembalian.setDate(null);
                    jBayar.setText(null);
                }
                catch(Exception ex){
                    JOptionPane.showMessageDialog(null, ex.getMessage());
                }
                this.setVisible(false);
                KasirBon kasirbon = new KasirBon();
                kasirbon.setVisible(true);
            }
        }
    }//GEN-LAST:event_jButton9ActionPerformed

    private void jButton10ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton10ActionPerformed
        // TODO add your handling code here:
        try{
            con = Koneksi.getConnection();
            stm = con.createStatement();
            String SQL = "select * from keranjang";
            rs = stm.executeQuery(SQL);
            while(rs.next()){
                con = Koneksi.getConnection();
                stm2 = con.createStatement();
                
                String SQL2 = "select kode_barang, jumlah from barang where kode_barang = '"+rs.getString("kode_barang")+"'";
                rs2 = stm2.executeQuery(SQL2);
                rs2.next();
                int qty = rs.getInt("jumlah");
                int jumlah = rs2.getInt("jumlah");
                int update_jumlah = qty+jumlah;
                
                pst = con.prepareStatement("update barang set jumlah = ? where kode_barang = '"+rs.getString("kode_barang")+"'");
                pst.setInt(1, update_jumlah);
                pst.executeUpdate();
            }
            con.createStatement().executeUpdate("delete from keranjang");
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
        this.setVisible(false);
    }//GEN-LAST:event_jButton10ActionPerformed

